import {
  SET_HEROES,
  SET_HERO_LIST_LOADING,
  SET_HEROES_ERROR,
} from '../actions/heroes';

const initialState = {
  list: [],
  isLoading: false,
  error: null,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_HEROES:
      return {
        ...state,
        list: action.value,
        error: null,
      };
    case SET_HERO_LIST_LOADING:
      return {
        ...state,
        isLoading: action.value,
      };
    case SET_HEROES_ERROR:
      return {
        ...state,
        list: [],
        error: action.error,
        isLoading: false,
      };
    default:
      return state;
  }
};
