import React, { Fragment, useEffect } from 'react';

import Hero from '../Hero';
import * as heroesActions from '../../actions/heroes';
import { connect } from 'react-redux';

const HeroList = ({ heroes, error, getHeroes }) => {
  useEffect(() => {
    getHeroes();
  }, [getHeroes]);

  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1>
      {error ? error.message : listHeroes()}
    </Fragment>
  );
};
