import React, { Fragment, useState, useEffect } from 'react';

import * as charactersAPI from '../../services/characters';
import Hero from '../Hero';
import { setHeroListLoading } from '../../actions/loader';
import { connect } from 'react-redux';

const HeroList = ({ showLoader, hideLoader }) => {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    showLoader();
    let chars = charactersAPI.list().then(res => {
      return res.data.data.results;
    });

    setCharacters(chars);

    hideLoader();

    return () => {
      console.log('component unmounting');
    };
  }, [showLoader, hideLoader]);

  const listCharacters = () =>
    characters.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1> {listCharacters()}
    </Fragment>
  );
};

const mapDispatchToProps = dispatch => {
  return {
    showLoader: () => dispatch(setHeroListLoading(true)),
    hideLoader: () => dispatch(setHeroListLoading(false)),
  };
};

export default connect(
  null,
  mapDispatchToProps
)(HeroList);
