import React, { Fragment, useEffect, useState } from 'react';
import * as charactersAPI from '../../services/characters';
import Hero from '../Hero';

export default class Home extends React.Component {
  state = { characters: [] };

  componentDidMount = async () => {
    let characters = (await charactersAPI.list()).data.data.results;
    this.setState({ characters });
  };

  listCharacters = () =>
    this.state.characters.map(({ id, name, description, thumbnail }) => (
      <Hero
        id={id}
        name={name}
        description={description}
        thumbnail={thumbnail}
      />
    ));

  render() {
    return (
      <Fragment>
        <h1>Heroes</h1> {this.listCharacters()}
      </Fragment>
    );
  }
}
