import React, { Fragment, useEffect } from 'react';

import Comic from '../Comic';

import { useSelector } from 'react-redux';
import useActions from '../../hooks/useActions';

import * as comicsActions from '../../actions/comics';
import * as comicsSelectors from '../../selectors/heroes';

const ComicList = ({ comics, getComics }) => {
  useEffect(() => {
    getComics();
  }, [getComics]);

  const listComics = () =>
    comics.map(value => <Comic key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Comics</h1> {listComics()}
    </Fragment>
  );
};

const mapStateToProps = state => {
  return { comics: state.comics.list };
};

const mapDispatchToProps = dispatch => {
  return {
    getComics: () => dispatch(comicsActions.getComics()),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ComicList);
