import React, { Fragment, useEffect } from "react";

import Loader from "../Loader";

import { useSelector } from "react-redux";
import useActions from "../../hooks/useActions";

import * as bandSelectors from "../../selectors/selectedBand";
import * as bandActions from "../../actions/selectedBand";

import * as albumSelectors from "../../selectors/albums";
import * as albumActions from "../../actions/albums";

const BandPage = () => {
  const selectedBand = useSelector(bandSelectors.getSelectedBand);
  const isLoading = useSelector(bandSelectors.isSelectedBandLoading);

  const fetchBandByID = useActions(bandActions.fetchBandByID);
  const fetchAlbumsForBand = useActions(albumActions.fetchAlbumsForBand);

  useEffect(() => {
    fetchBandByID();
    fetchAlbumsForBand();
  }, [fetchBandByID]);

  return (
    <Fragment>
      {isLoading && <Loader />}
      <Fragment>
        <h1>Band</h1>
        {selectedBand.name}
      </Fragment>
    </Fragment>
  );
};

export default BandPage;
