import React, { Fragment } from 'react';
import { Route, Redirect, Switch } from 'react-router';
import Home from '../components/Home';
import Loader from '../components/Loader';
import Template from '../components/Template/index';

import { MEMES, EVENTS } from '../constants/routes';
import { connect } from 'react-redux';

const Routes = () => (
  <Fragment>
    {this.state.loading && <Loader />}
    <Template>
      <Switch>
        <Route
          exact
          path="/"
          component={props => <Home {...props} setLoading={this.setLoading} />}
        />
        <Route exact path={MEMES} component={() => <div>memes</div>} />
        <Route exact path={EVENTS} component={() => <div>eventos</div>} />
      </Switch>
    </Template>
  </Fragment>
);

const mapStateToProps = state => {
  return { isLoading: state.loading };
};

export default connect(mapStateToProps)(Routes);
