import React, { Fragment, useEffect } from "react";

import Band from "../Band";
import Loader from "../Loader";

import { useSelector } from "react-redux";
import useActions from "../../hooks/useActions";

import * as bandSelectors from "../../selectors/selectedBand";
import * as bandActions from "../../actions/selectedBand";

const BandPage = ({ match }) => {
  const id = match.params.id;
  const selectedBand = useSelector(bandSelectors.selectedBand);
  const isLoading = useSelector(bandSelectors.isSelectedBandLoading);
  const fetchBandByID = useActions(bandActions.fetchBandByID, id);

  useEffect(() => {
    fetchBandByID();
  }, [fetchBandByID]);

  return (
    <Fragment>
      {isLoading && <Loader />}
      <Fragment>
        <h1>Band</h1>
        {selectedBand.name}
      </Fragment>
    </Fragment>
  );
};

export default BandPage;
