import React from 'react';
import { HeroContainer } from './style';

const Hero = ({ id, thumbnail, name, description }) => (
  <HeroContainer key={id}>
    <img
      src={`${thumbnail.path}/portrait_medium.${thumbnail.extension}`}
      alt={`${name}`}
    />
    {name}
    {description}
  </HeroContainer>
);

export default Hero;
