import React, { Fragment, useEffect } from 'react';

import Hero from '../Hero';
import propTypes from 'prop-types';

const HeroList = ({ heroes, error, getHeroes }) => {
  useEffect(() => {
    getHeroes();
  }, [getHeroes]);

  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1>
      {error ? error.message : listHeroes()}
    </Fragment>
  );
};

HeroList.defaultProps = { heroes: [], error: null, getHeroes: () => {} };
HeroList.propTypes = {
  heroes: propTypes.arrayOf(propTypes.object),
  error: propTypes.object,
  getHeroes: propTypes.func,
};

export default HeroList;
