import React, { Fragment } from 'react';
import Header from '../Header';
import { MEMES, EVENTS } from '../../constants/routes.js';

const Template = ({ children }) => (
  <Fragment>
    <Header
      routes={[
        { displayName: 'Memes', to: MEMES },
        { displayName: 'Events', to: EVENTS },
      ]}
    />
    {children}
  </Fragment>
);

export default Template;

