import React, { Fragment } from 'react';
import { BoardRow, Status } from './style';

import Square from '../Square';

import { useSelector } from 'react-redux';

import * as tttSelectors from '../../selectors/tictactoe';

const Board = () => {
  const board = useSelector(tttSelectors.getBoard);
  const winner = useSelector(tttSelectors.getWinner);
  const isXTurn = useSelector(tttSelectors.getXTurn);

  const getStatus = () => {
    if (winner) {
      return `${winner} won!`;
    } else {
      return `Current player: ${isXTurn ? 'X' : 'O'}`;
    }
  };

  return (
    <Fragment>
      <Status>{getStatus()}</Status>
      <BoardRow>
        <Square index="0">{board[0]}</Square>
        <Square index="1">{board[1]}</Square>
        <Square index="2">{board[2]}</Square>
      </BoardRow>
      <BoardRow>
        <Square index="3">{board[3]}</Square>
        <Square index="4">{board[4]}</Square>
        <Square index="5">{board[5]}</Square>
      </BoardRow>
      <BoardRow>
        <Square index="6">{board[6]}</Square>
        <Square index="7">{board[7]}</Square>
        <Square index="8">{board[8]}</Square>
      </BoardRow>
    </Fragment>
  );
};

export default Board;
