import React, { Fragment, useEffect, useState } from 'react';
import * as charactersAPI from '../../services/characters';

// const Home = () => {
//   return <p>Home</p>;
// };

export default class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = { characters: [] };
  }

  async componentDidMount() {
    let characters = (await charactersAPI.list()).data.data.results;
    this.setState({ characters });
  }

  render() {
    return (
      <div>
        {this.state.characters.map(val => (
          <div key={val.id}>{val.name}</div>
        ))}
      </div>
    );
  }
}
// export default Home;
