import React, { Fragment } from 'react';
import { BoardRow, Status } from './style';

import Square from '../Square';

import { useSelector } from 'react-redux';
import useActions from '../../hooks/useActions';

import * as tttSelectors from '../../selectors/tictactoe';
import * as tttActions from '../../actions/tictactoe';

const Board = ({ status }) => {
  const board = useSelector(tttSelectors.getBoard);
  const winner = useSelector(tttSelectors.getWinner);
  const isXTurn = useSelector(tttSelectors.getXTurn);
  const setWinner = useActions();

  return (
    <Fragment>
      <Status>Current player: {isXTurn ? 'X' : 'O'}</Status>
      <BoardRow>
        <Square>1</Square>
        <Square>2</Square>
        <Square>3</Square>
      </BoardRow>
      <BoardRow>
        <Square>4</Square>
        <Square>5</Square>
        <Square>6</Square>
      </BoardRow>
      <BoardRow>
        <Square>7</Square>
        <Square>8</Square>
        <Square>9</Square>
      </BoardRow>
    </Fragment>
  );
};

export default Board;
