import React, { Fragment, useEffect, useState } from 'react';
import * as charactersAPI from '../../services/characters';

// const Home = () => {
//   return <p>Home</p>;
// };

export default class Home extends React.Component {
  state = { characters: [] };

  async componentDidMount() {
    let characters = (await charactersAPI.list()).data.data.results;
    this.setState({ characters });
  }

  render() {
    return this.state.characters.map(({ id, name }) => (
      <div key={id}>{name}</div>
    ));
  }
}
