import React, { Fragment, useEffect } from "react";

import Band from "../Band";
import Loader from "../Loader";

import { useSelector } from "react-redux";
import useActions from "../../hooks/useActions";

import * as bandsSelectors from "../../selectors/bands";
import * as bandsActions from "../../actions/bands";

const BandPage = ({ match }) => {
  const id = match.params.id;
  const selectedBand = useSelector(bandsSelectors.selectedBand);

  if (!selectedBand) {
    const setSelectedBand = useActions(id => bandsActions.fetchBandByID(id));
  }

  return (
    <Fragment>
      {/* {isLoading && <Loader />} */}
      <Fragment>
        <h1>Band</h1>
        {selectedBand.name}
      </Fragment>
    </Fragment>
  );
};

export default BandPage;
