import React, { Fragment } from 'react';

import Hero from '../Hero';
import { setHeroListLoading } from '../../actions/loader';
import * as heroesActions from '../../actions/heroes';
import { connect } from 'react-redux';

const HeroList = ({ heroes }) => {
  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1> {listHeroes()}
    </Fragment>
  );
};

const mapStateToProps = state => {
  return { heroes: state.heroes.list };
};

const mapDispatchToProps = dispatch => {
  return {
    getHeroes: () => dispatch(heroesActions.getHeroes()),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HeroList);
