import React from 'react';
import { ComicContainer } from './style';

const Comic = ({ id, thumbnail, title, description }) => (
  <ComicContainer key={id}>
    <img
      src={`${thumbnail.path}/portrait_medium.${thumbnail.extension}`}
      alt="Hero's Thumbnail"
    />
    {title}
    {description}
  </ComicContainer>
);

export default Comic;
