import React from "react";
import { BandContainer } from "./style";
import { Link } from "react-router-dom";

import { BAND } from "../../constants/routes";

import useActions from "../../hooks/useActions";

import * as bandActions from "../../actions/selectedBand";

const Band = ({ id, name, biography, image }) => {
  const setSelectedBandID = useActions(bandActions.setSelectedBandID);

  return (
    <BandContainer>
      <img src={image} alt={name} />
      <p>{name}</p>
      <p>{biography}</p>
      <Link to={`${BAND}/${id}`}>
        <button onClick={id => setSelectedBandID(id)}>go to band</button>
      </Link>
    </BandContainer>
  );
};

export default Band;
