import HeroList from '../../components/HeroList';
import * as heroesActions from '../../actions/heroes';
import { connect } from 'react-redux';
import { getHeroes, getLoading, getError } from '../../selectors/heroes';

const mapStateToProps = state => {
  return { heroes: state.heroes.list, error: state.heroes.error };
};

const mapDispatchToProps = dispatch => {
  return {
    getHeroes: () => dispatch(heroesActions.getHeroes()),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HeroList);
