import React, { Fragment } from 'react';
import { BoardRow, Status } from './style';

import Square from '../Square';

import { useSelector } from 'react-redux';
import useActions from '../../hooks/useActions';

import * as tttSelectors from '../../selectors/tictactoe';
import * as tttActions from '../../actions/tictactoe';

const Board = () => {
  const board = useSelector(tttSelectors.getBoard);
  const winner = useSelector(tttSelectors.getWinner);
  const isXTurn = useSelector(tttSelectors.getXTurn);

  const setBoard = useActions(tttActions.setBoard);

  const getStatus = () => {
    if (winner) {
      return `${winner} won!`;
    } else {
      return `Current player: ${isXTurn ? 'X' : 'O'}`;
    }
  };

  return (
    <Fragment>
      <Status>{getStatus()}</Status>
      <BoardRow>
        <Square>{board[0]}</Square>
        <Square>{board[1]}</Square>
        <Square>{board[2]}</Square>
      </BoardRow>
      <BoardRow>
        <Square>{board[3]}</Square>
        <Square>{board[4]}</Square>
        <Square>{board[5]}</Square>
      </BoardRow>
      <BoardRow>
        <Square>{board[6]}</Square>
        <Square>{board[7]}</Square>
        <Square>{board[8]}</Square>
      </BoardRow>
    </Fragment>
  );
};

export default Board;
