import React, { Fragment, useEffect } from 'react';

import Hero from '../Hero';
import propTypes from 'prop-types';

import { useSelector } from 'react-redux';
import * as selectors from '../../selectors/heroes';

const HeroList = ({ getHeroes }) => {
  const heroes = useSelector(selectors.getHeroes);
  const error = useSelector(selectors.getError);

  useEffect(() => {
    getHeroes();
  }, [getHeroes]);

  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1>
      {error ? error.message : listHeroes()}
    </Fragment>
  );
};

HeroList.defaultProps = { heroes: [], error: null, getHeroes: () => {} };
HeroList.propTypes = {
  heroes: propTypes.arrayOf(propTypes.object),
  error: propTypes.object,
  getHeroes: propTypes.func.isRequired,
};

export default HeroList;
