import {
  SET_ALBUMS,
  SET_LOADING_ALBUMS,
  SET_ALBUMS_ERROR
} from "../actions/albums";

const initialState = {
  list: [],
  byBand: {},
  isLoading: false,
  error: null
};

const setAlbumsByBand = state => {
  const { id, albums } = state.band;
  const albumList = state.albums;

  console.log("indexing albums", state);

  const albumsByBand = albumList.reduce((indexedAlbums, cur) => {
    console.log("current", cur);
    for (const albumID of albums) {
      if (albumID === cur.id) {
        if (!indexedAlbums[id]) {
          indexedAlbums[id] = [];
        }

        indexedAlbums[id].concat(cur);
      }
    }

    return indexedAlbums;
  }, {});

  Object.assign(state.byBand[id], albumsByBand);
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_ALBUMS:
      console.log("set albums");
      const byBand = setAlbumsByBand(state);

      return {
        ...state,
        list: action.value,
        byBand,
        error: null
      };
    case SET_LOADING_ALBUMS:
      return {
        ...state,
        isLoading: action.value
      };
    case SET_ALBUMS_ERROR:
      return {
        ...state,
        list: [],
        byBand: {},
        error: action.error,
        isLoading: false
      };
    default:
      return state;
  }
};
