import React, { Fragment } from 'react';

import * as charactersAPI from '../../services/characters';
import Hero from '../Hero';

export default class HeroList extends React.Component {
  state = { characters: [] };

  componentDidMount = async () => {
    let characters = (await charactersAPI.list()).data.data.results;
    this.setState({ characters });
  };

  listCharacters = () =>
    this.state.characters.map(value => <Hero {...value} />);

  render() {
    return (
      <Fragment>
        <h1>Heroes</h1> {this.listCharacters()}
      </Fragment>
    );
  }
}
