import * as comicsAPI from '../services/comics';

export const getComics = () => {
  return (dispatch, getState) => {
    const state = getState();
    dispatch(setComicListLoading(true));

    comicsAPI.list().then(data => {
      const comics = data.data.data.results;
      dispatch(setComics(comics));

      dispatch(setComicListLoading(false));
      if (!state.heroes.isLoading) {
        dispatch(setLoader(false));
      }
    });
  };
};

export const SET_COMICS = 'SET_COMICS';
export const setComics = comics => {
  return { type: SET_COMICS, value: comics };
};

export const SET_COMIC_LIST_LOADING = 'SET_COMIC_LIST_LOADING';
export const setComicListLoading = isLoading => {
  return { type: SET_COMIC_LIST_LOADING, value: isLoading };
};
