export const SET_LOADER = 'SET_LOADER';
export const setLoader = isLoading => {
  return { type: SET_LOADER, value: isLoading };
};

export const SET_COMIC_LIST_LOADING = 'SET_COMIC_LIST_LOADING';
export const setComicListLoading = isLoading => {
  return { type: SET_COMIC_LIST_LOADING, value: isLoading };
};
