import React, { Fragment, useState, useEffect } from 'react';

import * as comicsAPI from '../../services/comics';
import Comic from '../Comic';
import { setComicListLoading } from '../../actions/loader';
import { connect } from 'react-redux';

const ComicList = ({ showLoader, hideLoader }) => {
  const [comics, setComics] = useState([]);

  useEffect(() => {
    showLoader();

    comicsAPI.list().then(data => {
      const chars = data.data.data.results;
      setComics(chars);

      hideLoader();
    });

    console.log(comics);

    setComics(comics);

    hideLoader();

    return () => {
      console.log('component unmounting');
    };
  }, [showLoader, hideLoader]);

  const listComics = () =>
    comics.map(value => <Comic key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Comics</h1> {listComics()}
    </Fragment>
  );
};

const mapDispatchToProps = dispatch => {
  return {
    showLoader: () => dispatch(setComicListLoading(true)),
    hideLoader: () => dispatch(setComicListLoading(false)),
  };
};

export default connect(
  null,
  mapDispatchToProps
)(ComicList);
