import React from 'react';
import { HeroContainer } from './style';

const Hero = ({ name, description, thumbnail }) => (
  <HeroContainer>
    <img
      src={`${thumbnail.path}/portrait_medium.${thumbnail.extension}`}
      alt={`${name}`}
    />
    <p>{name}</p>
    <p>{description}</p>
  </HeroContainer>
);

export default Hero;
