import React from 'react';
import { Route, Redirect, Switch } from 'react-router';
import Home from '../components/Home';
import Loader from '../components/Loader';
import Template from '../components/Template/index';

import { MEMES, EVENTS } from '../constants/routes';

// const Routes = () => (
// <Template>
//   <Switch>
//     <Route exact path="/" component={Home} />
//     <Route exact path={MEMES} component={() => <div>memes</div>} />
//     <Route exact path={EVENTS} component={() => <div>eventos</div>} />
//   </Switch>
// </Template>
// );

// export default Routes;

export default class Routes extends React.Component {
  state = { loading: false };

  showContent = () => (
    <Template>
      <Switch>
        <Route
          exact
          path="/"
          component={props => (
            <Home {...props} toggleLoading={this.toggleLoading} />
          )}
        />
        <Route exact path={MEMES} component={() => <div>memes</div>} />
        <Route exact path={EVENTS} component={() => <div>eventos</div>} />
      </Switch>
    </Template>
  );

  toggleLoading = loading => {
    this.setState({ loading });
  };

  render() {
    const { children } = this.props;
    return this.state.loading ? <Loader /> : this.showContent(children);
  }
}
