import * as charactersAPI from '../services/characters';
import { setLoader } from './loader';

export const getHeroes = () => {
  return (dispatch, getState) => {
    const state = getState();
    dispatch(setHeroListLoading(true));
    dispatch(setLoader(true));

    charactersAPI.list().then(data => {
      const heroes = data.data.data.results;
      dispatch(setHeroes(heroes));

      dispatch(setHeroListLoading(false));
      if (!state.comics.isLoading) {
        dispatch(setLoader(false));
      }
    });
  };
};

export const SET_HEROES = 'SET_HEROES';
export const setHeroes = heroes => {
  return { type: SET_HEROES, value: heroes };
};

export const SET_HERO_LIST_LOADING = 'SET_HERO_LIST_LOADING';
export const setHeroListLoading = isLoading => {
  return { type: SET_HERO_LIST_LOADING, value: isLoading };
};

export const SET_HEROES_ERROR = 'SET_HEROES_ERROR';
export const setHeroesError = error => {
  return { type: SET_HEROES_ERROR, error };
};
