import React from 'react';
import { SquareContainer } from './style';

import { useSelector } from 'react-redux';
import useActions from '../../hooks/useActions';

import * as tttSelectors from '../../selectors/tictactoe';
import * as tttActions from '../../actions/tictactoe';

const Square = ({ index, children }) => {
  const board = useSelector(tttSelectors.getBoard);
  const winner = useSelector(tttSelectors.getWinner);
  const isXTurn = useSelector(tttSelectors.getXTurn);
  const setBoard = useActions(tttActions.setBoard);

  const play = () => {
    if (winner) {
      return;
    }

    const move = isXTurn ? 'X' : 'O';
    const newBoard = board;
    newBoard[index] = move;
    setBoard(newBoard);
  };

  return <SquareContainer onClick={play}>{children}</SquareContainer>;
};

export default Square;
