import React, { Fragment, useEffect } from 'react';

import Hero from '../Hero';
import * as heroesActions from '../../actions/heroes';
import { connect } from 'react-redux';

const HeroList = ({ heroes, getHeroes }) => {
  useEffect(() => {
    getHeroes();
  }, [getHeroes]);

  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1> {listHeroes()}
    </Fragment>
  );
};

const mapStateToProps = state => {
  return { heroes: state.heroes.list };
};

const mapDispatchToProps = dispatch => {
  return {
    getHeroes: () => dispatch(heroesActions.getHeroes()),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HeroList);
