import React, { Fragment } from 'react';
import Header from '../Header';
import Loader from '../Loader';
import { MEMES, EVENTS } from '../../constants/routes.js';

// const Template = ({ children }) => (
//   <Fragment>
//     <Header
//       routes={[
//         { displayName: 'Memes', to: MEMES },
//         { displayName: 'Events', to: EVENTS },
//       ]}
//     />
//     {children}
//   </Fragment>
// );

// export default Template;

export default class Template extends React.Component {
  state = { loading: false };

  showContent = children => (
    <Fragment>
      <Header
        routes={[
          { displayName: 'Memes', to: MEMES },
          { displayName: 'Events', to: EVENTS },
        ]}
      />
      {children}
    </Fragment>
  );

  toggleLoading = loading => {
    setState({ loading });
  };

  render() {
    const { children } = this.props;
    return this.state.loading ? <Loader /> : this.showContent(children);
  }
}
