import React, { Fragment } from 'react';

import * as charactersAPI from '../../services/characters';
import Hero from '../Hero';
import { setLoader } from '../../actions/loader';
import { connect } from 'react-redux';

class HeroList extends React.Component {
  state = { characters: [] };

  componentDidMount = async () => {
    this.props.setLoading(true);
    let characters = (await charactersAPI.list()).data.data.results;

    this.setState({ characters }, () => {
      this.props.setLoading(false);
    });
  };

  listCharacters = () =>
    this.state.characters.map(value => <Hero key={value.id} {...value} />);

  render() {
    return (
      <Fragment>
        <h1>Heroes</h1> {this.listCharacters()}
      </Fragment>
    );
  }
}

const mapStateToProps = state => {
  return { isLoading: state.loading };
};

const mapDispatchToProps = dispatch => {
  return {
    showLoader: () => dispatch(setLoader(true)),
    hideLoader: () => dispatch(setLoader(false)),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HeroList);
