import React, { Fragment, useState, useEffect } from 'react';

import Hero from '../Hero';
import { setHeroListLoading } from '../../actions/loader';
import { connect } from 'react-redux';

const HeroList = ({ showLoader, hideLoader }) => {
  const [heroes] = useState([]);

  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1> {listHeroes()}
    </Fragment>
  );
};

const mapStateToProps = state => {
  return { heroes: state.heroes };
};

const mapDispatchToProps = dispatch => {
  return {
    showLoader: () => dispatch(setHeroListLoading(true)),
    hideLoader: () => dispatch(setHeroListLoading(false)),
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HeroList);
