import React, { Fragment, useEffect } from 'react';

import Comic from '../Comic';

import { useSelector } from 'react-redux';
import useActions from '../../hooks/useActions';

import * as comicsActions from '../../actions/comics';
import * as comicsSelectors from '../../selectors/comics';

const ComicList = () => {
  const comics = useSelector(comicsSelectors.getComics);
  const error = useSelector(comicsSelectors.getError);
  const getComics = useActions(comicsActions.getComics);

  useEffect(() => {
    getComics();
  }, [getComics]);

  const listComics = () =>
    comics.map(value => <Comic key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Comics</h1>
      {error ? error.message : listComics()}
    </Fragment>
  );
};

export default ComicList;
