import * as albumsAPI from "../services/bands";

export const fetchAlbumsForBand = () => {
  return (dispatch, getState) => {
    dispatch(setLoadingAlbums(true));

    const state = getState();
    const bandID = state.selectedBand.band.id;
    const bandAlbums = state.selectedBand.band.albums;

    if (bandID && bandAlbums) {
      let albums = state.albums.list;
      if (!albums) {
        albumsAPI
          .list()
          .then(response => {
            albums = response.data;
            dispatch(setAlbums(albums));

            console.log("setting albums by band", albums, bandAlbums);

            const albumsByBand = albums.reduce((indexedAlbums, cur) => {
              console.log("current", cur);
              for (const albumID of bandAlbums) {
                if (albumID === cur.id) {
                  indexedAlbums[bandID] = cur;
                }
              }

              return indexedAlbums;
            }, {});
          })
          .catch(err => {
            dispatch(setAlbumsError(err));
          });
      }

      dispatch(setAlbumsByBand(albumsByBand));
      dispatch(setLoadingAlbums(false));
    }
  };
};

export const SET_ALBUMS = "SET_ALBUMS";
export const setAlbums = albums => {
  return { type: SET_ALBUMS, value: albums };
};

export const SET_ALBUMS_BY_BAND = "SET_ALBUMS_BY_BAND";
export const setAlbumsByBand = albums => {
  return { type: SET_ALBUMS_BY_BAND, value: albums };
};

export const SET_LOADING_ALBUMS = "SET_LOADING_ALBUMS";
export const setLoadingAlbums = isLoading => {
  return { type: SET_LOADING_ALBUMS, value: isLoading };
};

export const SET_ALBUMS_ERROR = "SET_ALBUMS_ERROR";
export const setAlbumsError = error => {
  return { type: SET_ALBUMS_ERROR, error };
};
