import {
  SET_LOADER,
  SET_COMIC_LIST_LOADING,
  SET_HERO_LIST_LOADING,
} from '../actions/loader';

const initialState = {
  isLoading: false,
  isHeroListLoading: false,
  isComicListLoading: false,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_LOADER:
      return {
        ...state,
        isLoading: action.value,
      };
    case SET_COMIC_LIST_LOADING:
      return {
        ...state,
        isHeroListLoading: action.value,
        isLoading: action.value || state.isComicListLoading,
      };
    default:
      return state;
  }
};
