import {
  SET_ALBUMS,
  SET_LOADING_ALBUMS,
  SET_ALBUMS_ERROR
} from "../actions/albums";

const initialState = {
  list: [],
  byBand: {},
  isLoading: false,
  error: null
};

const setAlbumsByBand = (albumList, bandID) => {
  console.log("indexing albums", albumList, bandID);

  const albumsByBand = albumList.reduce((indexedAlbums, cur) => {
    console.log("current", cur);
    for (const albumID of albumList) {
      if (albumID === cur.id) {
        if (!indexedAlbums[bandID]) {
          indexedAlbums[bandID] = [];
        }

        indexedAlbums[bandID].concat(cur);
      }
    }

    return indexedAlbums;
  }, {});

  Object.assign(state.byBand[id], albumsByBand);
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_ALBUMS:
      console.log("set albums", action.value);
      const byBand = setAlbumsByBand(action.value.albums, action.value.id);

      return {
        ...state,
        list: action.value.albums,
        byBand,
        error: null
      };
    case SET_LOADING_ALBUMS:
      return {
        ...state,
        isLoading: action.value
      };
    case SET_ALBUMS_ERROR:
      return {
        ...state,
        list: [],
        byBand: {},
        error: action.error,
        isLoading: false
      };
    default:
      return state;
  }
};
