export const SET_BOARD = 'SET_BOARD';
export const setBoard = board => {
  return { type: SET_BOARD, value: board };
};

export const SET_WINNER = 'SET_WINNER';
export const setWinner = winner => {
  return { type: SET_WINNER, value: winner };
};

export const SET_X_TURN = 'SET_X_TURN';
export const setXTurn = isXTurn => {
  return { type: SET_X_TURN, value: isXTurn };
};
