import React, { Fragment } from 'react';

import * as comicsAPI from '../../services/comics';
import Comic from '../Comic';

class HeroList extends React.Component {
  state = { comics: [] };

  componentDidMount = async () => {
    this.props.showLoader();
    let comics = (await comicsAPI.list()).data.data.results;

    this.setState({ comics }, () => {
      this.props.hideLoader();
    });
  };

  listComics = () =>
    this.state.comics.map(value => <Comic key={value.id} {...value} />);

  render() {
    return (
      <Fragment>
        <h1>Comics</h1> {this.listComics()}
      </Fragment>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    showLoader: () => dispatch(setLoader(true)),
    hideLoader: () => dispatch(setLoader(false)),
  };
};

export default connect(
  null,
  mapDispatchToProps
)(ComicList);
