import * as bandsAPI from "../services/bands";

export const fetchBands = () => {
  return dispatch => {
    dispatch(setLoadingBandList(true));

    bandsAPI
      .list()
      .then(response => {
        const bands = response.data;
        dispatch(setBands(bands));

        dispatch(setLoadingBandList(false));
      })
      .catch(err => {
        dispatch(setBandsError(err));
      });
  };
};

export const fetchBandByID = id => {
  return dispatch => {
    dispatch(setLoadingSelectedBand(true));

    bandsAPI
      .bandByID(id)
      .then(response => {
        const bands = response.data;
        dispatch(setSelectedBand(bands));

        dispatch(setLoadingSelectedBand(false));
      })
      .catch(err => {
        dispatch(setSelectedBandError(err));
      });
  };
};

export const SET_BANDS = "SET_BANDS";
export const setBands = heroes => {
  return { type: SET_BANDS, value: heroes };
};

export const SET_LOADING_BAND_LIST = "SET_LOADING_BAND_LIST";
export const setLoadingBandList = isLoading => {
  return { type: SET_LOADING_BAND_LIST, value: isLoading };
};

export const SET_BANDS_ERROR = "SET_BANDS_ERROR";
export const setBandsError = error => {
  return { type: SET_BANDS_ERROR, error };
};

export const SET_SELECTED_BAND = "SET_SELECTED_BAND";
export const setSelectedBand = selectedBand => {
  return { type: SET_SELECTED_BAND, value: selectedBand };
};

export const SET_LOADING_SELECTED_BAND = "SET_LOADING_SELECTED_BAND";
export const setLoadingSelectedBand = isLoading => {
  return { type: SET_LOADING_SELECTED_BAND, value: isLoading };
};

export const SET_SELECTED_BAND_ERROR = "SET_SELECTED_BAND_ERROR";
export const setSelectedBandError = error => {
  return { type: SET_SELECTED_BAND_ERROR, error };
};
