import React, { Fragment, useEffect, useState } from 'react';
import * as charactersAPI from '../../services/characters';

import styled from 'styled-components';

const Hero = styled.div`
  background-color: aquamarine;
`;

export default class Home extends React.Component {
  state = { characters: [] };

  async componentDidMount() {
    let characters = (await charactersAPI.list()).data.data.results;
    this.setState({ characters });
  }

  render() {
    return (
      <Fragment>
        <h1>Heroes</h1> {this.listCharacters()}
      </Fragment>
    );
  }

  listCharacters() {
    return this.state.characters.map(({ id, name, description, thumbnail }) => (
      <Hero key={id}>
        <img
          src={`${thumbnail.path}/portrait_medium.${thumbnail.extension}`}
          alt="Hero's Thumbnail"
        />
        {name}
        {description}
      </Hero>
    ));
  }
}
