import React, { Fragment, useEffect } from 'react';

import Hero from '../Hero';

import { useSelector } from 'react-redux';
import useActions from '../../hooks/useActions';

import * as selectors from '../../selectors/heroes';
import * as actions from '../../actions/heroes';

const HeroList = () => {
  const heroes = useSelector(selectors.getHeroes);
  const error = useSelector(selectors.getError);
  const getHeroes = useActions(actions.getHeroes);

  useEffect(() => {
    getHeroes();
  }, [getHeroes]);

  const listHeroes = () =>
    heroes.map(value => <Hero key={value.id} {...value} />);

  return (
    <Fragment>
      <h1>Heroes</h1>
      {error ? error.message : listHeroes()}
    </Fragment>
  );
};

export default HeroList;
