import React, { Fragment, useEffect, useState } from 'react';
import * as charactersAPI from '../../services/characters';
import Hero from '../Hero';

import styled from 'styled-components';

export default class Home extends React.Component {
  state = { characters: [] };

  componentDidMount = async () => {
    let characters = (await charactersAPI.list()).data.data.results;
    this.setState({ characters });
  };

  listCharacters = () =>
    this.state.characters.map(({ id, name, description, thumbnail }) => (
      <Hero key={id}>
        <img
          src={`${thumbnail.path}/portrait_medium.${thumbnail.extension}`}
          alt="Hero's Thumbnail"
        />
        {name}
        {description}
      </Hero>
    ));

  render() {
    return (
      <Fragment>
        <h1>Heroes</h1> {this.listCharacters()}
      </Fragment>
    );
  }
}
